    background: radial-gradient(circle at center, #708090 0%, #001020 100%);
    text-shadow: 2px 2px 16px #2F2F2F;
    position: relative;
    overflow: hidden;
  }

  section {
    padding: 24px;
    width: 288px;
    height: 288px;
    background: radial-gradient(circle at center, #708080 0%, #102030 100%);
    border-radius: 50%;
    z-index: 1;
  }

  #divPageBackground {
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    position: absolute;
    background: transparent
  }

  .logo {
    opacity: 0;
    display: inline;
    position: absolute;
    transform-origin: -25% -25%;
  }
</style>